package com;

public class if_else_Statement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		if(a<5)
		{
			System.out.println(""+a);
			
		}
		else if(a>=6 && a<=15)
		{
			System.out.println("in between "+a);
		}
		else
		{
			System.out.println("more than 5 i.e : "+a);
		}
	

	}

}
